      BEGIN
      EXEC dbo.sp_executesql @statement = N'
      CREATE PROCEDURE [pbs_PO_SUPPLIER_EVALUATION_{XXX}_InsertUpdate]
      @SUPPLIER_CODE varchar(15),
      @ITEM_CODE varchar(15),
      @EFFECTIVE_DATE int,
      @SUSPENDED char(1),
      @VALUE0 decimal(18, 2),
      @VALUE1 decimal(18, 2),
      @VALUE2 decimal(18, 2),
      @VALUE3 decimal(18, 2),
      @VALUE4 decimal(18, 2),
      @VALUE5 decimal(18, 2),
      @VALUE6 decimal(18, 2),
      @VALUE7 decimal(18, 2),
      @VALUE8 decimal(18, 2),
      @VALUE9 decimal(18, 2),
      @NOTES nvarchar (200),
      @UPDATED int,
      @UPDATED_BY varchar(15)
      AS

      SET NOCOUNT ON

      IF EXISTS(SELECT SUPPLIER_CODE,ITEM_CODE FROM [pbs_PO_SUPPLIER_EVALUATION_{XXX}] WHERE [SUPPLIER_CODE] = @SUPPLIER_CODE AND [ITEM_CODE] = @ITEM_CODE)
      BEGIN
      UPDATE [pbs_PO_SUPPLIER_EVALUATION_{XXX}] SET
      [EFFECTIVE_DATE] = @EFFECTIVE_DATE,
      [SUSPENDED] = @SUSPENDED,
      [VALUE0] = @VALUE0,
      [VALUE1] = @VALUE1,
      [VALUE2] = @VALUE2,
      [VALUE3] = @VALUE3,
      [VALUE4] = @VALUE4,
      [VALUE5] = @VALUE5,
      [VALUE6] = @VALUE6,
      [VALUE7] = @VALUE7,
      [VALUE8] = @VALUE8,
      [VALUE9] = @VALUE9,
      [NOTES] = @NOTES,
      [UPDATED] = @UPDATED,
      [UPDATED_BY] = @UPDATED_BY
      WHERE [SUPPLIER_CODE] = @SUPPLIER_CODE AND [ITEM_CODE] = @ITEM_CODE
      END
      ELSE
      BEGIN
      INSERT INTO [pbs_PO_SUPPLIER_EVALUATION_{XXX}] (
      [SUPPLIER_CODE],
      [ITEM_CODE],
      [EFFECTIVE_DATE],
      [SUSPENDED],
      [VALUE0],
      [VALUE1],
      [VALUE2],
      [VALUE3],
      [VALUE4],
      [VALUE5],
      [VALUE6],
      [VALUE7],
      [VALUE8],
      [VALUE9],
      [NOTES],
      [UPDATED],
      [UPDATED_BY]
      ) VALUES (
      @SUPPLIER_CODE,
      @ITEM_CODE,
      @EFFECTIVE_DATE,
      @SUSPENDED,
